mapper.select:
select site_id, username, bet_amount as betamount,valid_bet_amount as validamount,net_amount as winlose, case when game_kind=22 and game_type!='HUNTER' then ifnull(bet_amount_bonus, 0.00) - ifnull(net_amount_bonus, 0.00) end as jackpot, case when (select id from ds_game_type where game_type = out_game_code) is null then 22000 else (select id from ds_game_type where game_type = out_game_code) end as game_type, case when (select game_name from ds_game_type where game_type = out_game_code and fk_live_id=2 and parent_id in (21,22)) is null then 'AG机率' else (select game_name from ds_game_type where game_type = out_game_code and fk_live_id=2 and parent_id in (21,22)) end as game_name, bet_time as bet_time,win_loss_type as winlose_type, bill_no as bill_no, table_code as table_no, game_code as serial_id, '' as shoe_infoid, round as round_no, result as result,before_credit from ds_ag_live
order by ${orderByClause}
limit ${page},${pageLimit}
