mapper.select:
   select  siteid as site_id,user_name as username, amount as betamount,amount  as validamount,wins as winlose,
       (select id from ds_game_type where lid = out_game_code and parent_id = #{gameKind}) as game_type, (select game_name from ds_game_type where lid = out_game_code and parent_id = #{gameKind}) as game_name,
       bet_time,win_lose_type as winlose_type, nid as bill_no,odd as odds,hit_detail as item,issue as qishu,pan
  from ds_guangfang_lottery
where 1=1${where}
order by ${order}

  limit ${limit}

   SELECT IFNULL(t.id,56000) as game_type, IFNULL(t.game_name,'KG新官方彩') game_name, COUNT(1) bet_count, SUM(d.amount) betamount, SUM(d.wins) winlose, SUM(d.amount) validamount
	FROM ds_guangfang_lottery d
	LEFT JOIN ds_game_type t ON d.lid = t.out_game_code and t.parent_id = #{gameKind}
	where 1${where}
	GROUP BY d.lid
