mapper.select:
SELECT site_id,table_id,username AS username,bet_money AS betamount,pay_off AS winlose,term AS qishu,bet_msg AS playInfo,bet_odds_expected AS odds,bet_odds1314 AS odds2, (SELECT id FROM ds_game_type WHERE game_type = out_game_code AND fk_live_id = 22 AND parent_id = 80 ) AS game_type, (SELECT game_name FROM ds_game_type WHERE game_type = out_game_code AND fk_live_id = 22 AND parent_id = 80 ) AS game_name, create_time AS bet_time,win_lose_type AS winlose_type,bet_billno AS bill_no, validBetMoney AS validamount FROM ds_manniu_bet
order by ${orderByClause}
limit ${page},${pageLimit}
