mapper.select:
    select site_id, username, bet_amount as betamount,valid_bet_amount as validamount,net_amount as winlose,
		bet_time as bet_time,win_loss_type as winlose_type, trade_no as bill_no,game_code as serial_id 
	from ds_ag_hunter




      order by ${orderByClause}


      limit ${page},${pageLimit}
