mapper.select:
 select  siteid as site_id,user as username, jiner as betamount,jinerb  as validamount,win as winlose,
     (select id from ds_game_type where lid = out_game_code and parent_id = 51) as game_type, (select game_name from ds_game_type where lid = out_game_code and parent_id = 51) as game_name,
     bet_time,win_lose_type as winlose_type, nid as bill_no,odds,itmes as item,project as play,content as play_info,qishu
from ds_jingdian_lottery




order by ${orderByClause}


limit ${page},${pageLimit}

   select  siteid as site_id,user as username, jiner as betamount,jinerb  as validamount,win as winlose,
       (select id from ds_game_type where lid = out_game_code and parent_id = #{gameKind}) as game_type, (select game_name from ds_game_type where lid = out_game_code and parent_id = #{gameKind}) as game_name,
       bet_time,win_lose_type as winlose_type, nid as bill_no,odds,itmes as item,project as play,content as play_info,qishu
  from ds_jingdian_lottery
where 1=1${where}
order by ${order}

  limit ${limit}

   SELECT IFNULL(t.id,51000) as game_type, IFNULL(t.game_name,'kg经典彩') game_name, COUNT(1) bet_count, SUM(d.jiner) betamount, SUM(d.win) winlose, SUM(d.jinerb) validamount
	FROM ds_jingdian_lottery d
	LEFT JOIN ds_game_type t ON d.lid = t.out_game_code and t.parent_id = #{gameKind}
	where 1${where}
	GROUP BY d.lid
