mapper.select:
select site_id, username, betting_amount as betamount,valid_amount as validamount,win_lose_amount as winlose, (select id from ds_game_type where game_name_id = out_game_code and fk_live_id=3 and parent_id = 70) as game_type, (select game_name from ds_game_type where game_name_id = out_game_code and fk_live_id=3 and parent_id = 70) as game_name, add_time as bet_time,win_lose_type as winlose_type, order_number as bill_no, table_id as table_no, game_result as result from ds_og_live
order by ${orderByClause}
limit ${page},${pageLimit}
