mapper.update:
update ds_report 


    bet_count = #{betCount,jdbcType=INTEGER},


    betamount = #{betamount,jdbcType=DECIMAL},


    winlose = #{winlose,jdbcType=DECIMAL},


    validamount = #{validamount,jdbcType=DECIMAL} 


where username = #{username,jdbcType=VARCHAR} AND game_type =#{gameType} AND bet_time = #{betTime}

mapper.select:

SELECT a.id,SUM(a.bet_count) AS COUNT,SUM(a.betamount) AS betMount,SUM(a.winlose) AS payoffMount,SUM(a.validamount) AS validateMount 
FROM ds_report a,ds_game_type b WHERE a.username=#{userName,jdbcType=VARCHAR} AND a.bet_time =#{date,jdbcType=VARCHAR} 
and b.out_game_code=#{gameType} AND a.game_type=b.id AND a.game_kind=51 and b.parent_id=51


SELECT  IF(COUNT(*)=0,NULL,COUNT(*))  AS COUNT,SUM(a.jiner) AS betMount,
SUM(a.jinerb) AS validateMount,SUM(a.win) AS payoffMount,
IFNULL((SELECT id FROM ds_game_type b WHERE b.out_game_code = a.lid AND b.parent_id = 51),51000) AS gameType 
FROM ds_jingdian_lottery a WHERE a.user= #{userName,jdbcType=VARCHAR} 
AND a.lid=#{lid,jdbcType=INTEGER} 
AND DATE_ADD(STR_TO_DATE(CONCAT(LEFT(a.time_draw,10),' ',SUBSTR(a.time_draw,12,8)),'%Y-%m-%d %H:%i:%s'),INTERVAL -12 HOUR) >=#{beforDate,jdbcType=VARCHAR} 
AND DATE_ADD(STR_TO_DATE(CONCAT(LEFT(a.time_draw,10),' ',SUBSTR(a.time_draw,12,8)),'%Y-%m-%d %H:%i:%s'),INTERVAL -12 HOUR) <= #{endDate,jdbcType=VARCHAR} 
AND a.win_lose_type<>4 
AND a.win_lose_type<>-1 
AND a.win_lose_type IS NOT NULL
